import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Select from 'components/Form/Select';
import { withRouter } from 'react-router';

import useForm from 'helpers/hooks/useForm';

import courses from 'constants/api/oprec';

import takeCode from 'helpers/takeCode';


import {
	statusCourses,
	fetchCourses,
	messageCourse,
} from 'store/actions/oprec';

export default function ListOfferedCourse({data}) {
	const [{ offered_course_id }, setState] = useForm({ offered_course_id: '' });

	const dispatch = useDispatch();
	const COURSES = useSelector((state) => state.courses);

	return (
		<div>
			<section className='flex flex-col mt-8'>
				<div className='flex items-center pb-24'>
					<div className='w-full md:w-4/6 xl:w-5/12 sm:w-6/12'>
						<Select
							labelName='Choose Course'
							name='offered_course_id'
							value={offered_course_id}
							fallbackText='Select your focus'
							onClick={setState}>
							<option value='Web Developer'>Web Designer</option>
						</Select>
						<div className='flex justify-center'>
							<button
								type='submit'
								className='w-full h-10 bg-blue-800 hover:bg-blue-700 transition-all duration-200 focus:outline-none shadow-inner text-white py-2 rounded-md'>
								Choose!
							</button>
						</div>
					</div>
				</div>
			</section>
		</div>
	);
}
