import {
	FETCH_OFFERED_COURSES,
	STATUS_OFFERED_COURSES,
	MESSAGE_OFFERED_COURSES,
} from 'constants/types/offered';

const initialState = {
	data: {},
	total: 0,
	status: 'idle',
	message: '',
};

export default function (state = initialState, action) {
	switch (action.type) {
		case STATUS_OFFERED_COURSES:
			return {
				...state,
				status: action.payload,
			};

		case FETCH_OFFERED_COURSES:
			return {
        ...state,
        data:
          action.payload?.reduce?.((acc, item) => {
            acc[item.id] = item;
            return acc;
          }, {}) ?? {},
        total: action.payload?.length ?? 0,
        status: "ok",
      };

		case MESSAGE_OFFERED_COURSES:
			return {
				...state,
				message: action.payload,
				status: 'error',
			};

		default:
			return state;
	}
}
