import React from 'react';
import propTypes from 'prop-types';

export default function Input({
	value,
	error,
	name,
	onChange,
	placeholder,
	labelName,
	inputClassName,
	type,
}) {
	return (
		<div className='flex flex-col mb-4'>
			{labelName && (
				<label
					htmlFor={name}
					className={[
						'text-lg mb-2',
						error ? 'text-red-500' : 'text-gray-900',
					].join(' ')}>
					{labelName}
				</label>
			)}
			<input
				name={name}
				onChange={onChange}
				type={type}
				className={[
					'bg-white focus:outline-none border w-full px-6 py-3 rounded-md',
					error
						? 'border-red-500 text-red-500'
						: 'focus:border-blue-500 border-gray-600 text-gray-600',
					inputClassName,
				].join(' ')}
				value={value}
				placeholder={placeholder ?? 'Please change placeholder'}
			/>
			<span className='text-red-500 pt-2'>{error}</span>
		</div>
	);
}

Input.propTypes = {
	error: propTypes.string,
	name: propTypes.string.isRequired,
	onChange: propTypes.func.isRequired,
	value: propTypes.oneOf([propTypes.string, propTypes.number]).isRequired,
	placeholder: propTypes.string,
	labelName: propTypes.string,
	inputClassName: propTypes.string,
	type: propTypes.oneOf(
		'text',
		'number',
		'password',
		'date',
		'date-range',
		'email',
		'tel'
	),
};
