import React, { useEffect } from 'react';

import { useDispatch, useSelector } from 'react-redux';

import oprec from 'constants/api/oprec';

import Sidebar from 'parts/Sidebar';
import OprecForm from 'parts/OprecForm';

import Loading from 'parts/Loading';
import {
	statusCourses,
	fetchCourses,
	messageCourse,
} from 'store/actions/oprec';

export default function OpenRecruitment() {
	const dispatch = useDispatch();
	const OPREC = useSelector((state) => state.oprec);

	useEffect(() => {
		window.scroll(0, 0);

		dispatch(statusCourses('loading'));
		oprec
			.myCourse()
			.then((res) => {
				console.log(res);
				dispatch(fetchCourses(res.data));
			})
			.catch((err) => {
				dispatch(messageCourse(err?.response?.data?.message ?? 'error'));
			});
	}, [dispatch]);

	function EmptyState() {
		return (
			<section className='flex h-screen items-center relative z-50 bg-white'>
				<div className='w-full sm:w-5/12 text-center py-12 mx-auto'>
					<p className='text-lg text-gray-600 mt-4 mb-8 mx-auto text-center'>
						It seems you don’t have any class yet so let’s get them and grow
						your skills
					</p>
				</div>
			</section>
		);
	}

	return (
		<div className='flex'>
			<Sidebar></Sidebar>
			<main className='flex-1'>
				<div className='px-4 sm:px-16'>
					{OPREC.status === 'loading' && <Loading></Loading>}
					{OPREC.status === 'error' && OPREC.message}
					{OPREC.status === 'ok' &&
						(OPREC.total > 0 ? (
							<>
								<section className='flex flex-col mt-8 pl-12 sm:pl-0'>
									<h1 className='text-xl sm:text-4xl text-gray-900 font-medium'>
										Open Recruitment
									</h1>
									<p className='text-sm sm:text-lg text-gray-600'>
										Choose what you're good at
									</p>
									{Object.values(OPREC.data)?.map?.((item, index) => {
										return (
											<OprecForm data={item.course}
											key={index}></OprecForm>
										);
									})}
								</section>
							</>
						) : (
							<EmptyState></EmptyState>
						))}
				</div>
			</main>
		</div>
	);
}
